func newMockHelm(version, store, namespace string) *Helm {
	return &Helm{
		Version:   version,
		Namespace: namespace,
		Kube:      getMockConfigInstance(),
		Store:     store,
		Instance: &api.Instance{
			TargetVersions: map[string]string{
				"k8s":          "v1.16.0",
				"istio":        "1.6.1",
				"cert-manager": "v0.15.0",
			},
			DeprecatedVersions: []api.Version{
				{
					Name:           "extensions/v1beta1",
					Kind:           "Deployment",
					DeprecatedIn:   "v1.9.0",
					RemovedIn:      "v1.16.0",
					ReplacementAPI: "apps/v1",
					Component:      "k8s",
				},
				{
					Name:           "apps/v1",
					Kind:           "Deployment",
					DeprecatedIn:   "",
					RemovedIn:      "",
					ReplacementAPI: "",
					Component:      "k8s",
				},
			},
			IgnoreDeprecations: false,
			IgnoreRemovals:     false,
			OutputFormat:       "normal",
		},
	}
}

func newBadKubeClient() (k *kube) {
	conf := new(rest.Config)
	conf.Host = "127.0.0.1:9999"
	k = new(kube)
	k.Client, _ = kubernetes.NewForConfig(conf)
	return
}

func Test_checkForAPIVersion(t *testing.T) {
	tests := []struct {
		name     string
		manifest []byte
		want     []*api.Output
		wantErr  bool
	}{
		{
			name:     "empty",
			manifest: []byte{},
			want:     []*api.Output{{}},
			wantErr:  true,
		},
		{
			name:     "got version",
			manifest: []byte("apiVersion: extensions/v1beta1\nkind: Deployment"),
			want:     []*api.Output{{APIVersion: &api.Version{Name: "extensions/v1beta1", Kind: "Deployment", DeprecatedIn: "v1.9.0", RemovedIn: "v1.16.0", ReplacementAPI: "apps/v1", Component: "k8s"}}},
			wantErr:  false,
		},
		{
			name:     "nil return",
			manifest: []byte("apiVersion: v1beta1\nkind: SomeOtherThing"),
			want:     nil,
			wantErr:  false,
		},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			h := newMockHelm("3", "configmap", "")
			got, err := h.checkForAPIVersion(tt.manifest)
			if tt.wantErr {
				assert.Error(t, err)
				return
			}
			assert.NoError(t, err)
			assert.EqualValues(t, tt.want, got)
		})
	}
}

func TestHelm_getManifestsVersionTwo(t *testing.T) {
	tests := []struct {
		name        string
		helmVersion string
		store       string
		namespace   string
		wantErr     bool
		errMessage  string
		configMap   *v1.ConfigMap
		secret      *v1.Secret
		want        []*api.Output
	}{
		{
			name:        "three - error",
			helmVersion: "3",
			wantErr:     true,
			errMessage:  "helm 2 function called without helm 2 version set",
		},
		{
			name:        "helm 2 valid configmap",
			helmVersion: "2",
			store:       "configmaps",
			configMap:   &helmConfigMap,
			want:        wantOutput,
		},
		{
			name:        "helm 2 valid secret",
			helmVersion: "2",
			store:       "secrets",
			secret:      &helmSecret2,
			want:        wantOutput,
		},
		{
			name:        "helm 2 bad store",
			helmVersion: "2",
			store:       "badstore",
			secret:      &helmSecret2,
			wantErr:     true,
			errMessage:  "helm-store should be configmap or secrets",
		},
		{
			name:        "helm 2 other namespace",
			namespace:   "demo1",
			helmVersion: "2",
			store:       "configmaps",
			configMap:   &helmConfigMap,
			want:        nil,
		},
		{
			name:        "helm 2 target namespace",
			namespace:   "demo1",
			helmVersion: "2",
			store:       "configmaps",
			configMap:   &helmConfigMap2,
			want:        wantOutputNamespaced,
		},
	}
	for _, tt := range tests {
		h := newMockHelm(tt.helmVersion, tt.store, tt.namespace)
		if tt.configMap != nil {
			_, err := h.Kube.Client.CoreV1().ConfigMaps(tt.namespace).Create(tt.configMap)
			if err != nil {
				t.Errorf("failed putting configMap in mocked kube. test: %s", tt.name)
			}
		}
		if tt.secret != nil {
			_, err := h.Kube.Client.CoreV1().Secrets(tt.namespace).Create(tt.secret)
			if err != nil {
				t.Errorf("failed putting secret in mocked kube. test: %s", tt.name)
			}
		}
		t.Run(tt.name, func(t *testing.T) {
			err := h.getReleasesVersionTwo()
			if tt.wantErr {
				assert.EqualError(t, err, tt.errMessage)
				return
			}
			assert.NoError(t, err)
			assert.Equal(t, tt.want, h.Instance.Outputs)
		})
	}
}

func TestHelm_getManifestsVersionThree(t *testing.T) {
	tests := []struct {
		name        string
		helmVersion string
		wantErr     bool
		errMessage  string
		secret      *v1.Secret
		want        []*api.Output
	}{
		{
			name:        "two - error",
			helmVersion: "2",
			wantErr:     true,
			errMessage:  "helm 3 function called without helm 3 version set",
		},
		{
			name:        "helm 3 valid",
			helmVersion: "3",
			secret:      &helmSecret,
			want:        wantOutput,
		},
	}

	for _, tt := range tests {
		h := newMockHelm(tt.helmVersion, "secrets", "")
		if tt.secret != nil {
			_, err := h.Kube.Client.CoreV1().Secrets("default").Create(tt.secret)
			if err != nil {
				t.Errorf("failed putting secret in mocked kube. test: %s", tt.name)
			}
		}
		t.Run(tt.name, func(t *testing.T) {
			err := h.getReleasesVersionThree()
			if tt.wantErr {
				assert.EqualError(t, err, tt.errMessage)
				return
			}
			assert.NoError(t, err)
			assert.Equal(t, tt.want, h.Instance.Outputs)
		})
	}
}

func TestHelm_getManifest_badClient(t *testing.T) {
	tests := []struct {
		name        string
		helmVersion string
		store       string
		wantErr     bool
		errMessage  string
		secret      *v1.Secret
		want        []*api.Output
	}{
		{
			name:        "two - bad client",
			helmVersion: "2",
			store:       "configmaps",
			wantErr:     true,
			errMessage:  "helm 3 function called without helm 3 version set",
		},
		{
			name:        "three - bad client",
			helmVersion: "3",
			wantErr:     true,
			errMessage:  "helm 3 function called without helm 3 version set",
		},
	}

	for _, tt := range tests {
		h := &Helm{
			Version: tt.helmVersion,
			Store:   tt.store,
			Kube:    newBadKubeClient(),
		}
		t.Run(tt.name, func(t *testing.T) {
			var err error
			switch tt.helmVersion {
			case "2":
				err = h.getReleasesVersionTwo()
			case "3":
				err = h.getReleasesVersionThree()
			}
			if tt.wantErr {
				assert.Error(t, err)
				assert.Contains(t, err.Error(), "connect: connection refused")
				return
			}
		})
	}
}

func TestHelm_FindVersions(t *testing.T) {
	tests := []struct {
		name        string
		helmVersion string
		wantErr     bool
		errMessage  string
	}{
		// Only adding this one test case since the others generally cross into other functions.
		{"one - err", "1", true, "helm version either not specified or incorrect (use 2 or 3)"},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			h := newMockHelm(tt.helmVersion, "", "secrets")
			err := h.FindVersions()
			if tt.wantErr {
				assert.EqualError(t, err, tt.errMessage)
			} else {
				assert.NoError(t, err)
			}
		})
	}
}

func Test_helmToRelease(t *testing.T) {
	tests := []struct {
		name        string
		helmRelease interface{}
		want        *Release
		wantErr     bool
		errMsg      string
	}{
		{
			name:        "test err in json.Marshal",
			helmRelease: map[string]interface{}{"foo": make(chan int)},
			want:        nil,
			wantErr:     true,
			errMsg:      "error marshaling release: json: unsupported type: chan int",
		},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			got, err := helmToRelease(tt.helmRelease)
			if tt.wantErr {
				assert.EqualError(t, err, tt.errMsg)
			} else {
				assert.NoError(t, err)
				assert.Equal(t, tt.want, got)
			}

		})
	}
}
